import React from "react";
import API from "../utils/booksapi";

export default class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      search: "",
    };
  }

  componentDidUpdate() {
    this.searchBooks(this.state.search);
  }

  handleFormSubmit = (event) => {
    var data = { search: document.getElementById("search").value };
    this.setState({ search: data.search });
    event.preventDefault();
    console.log(this.state.books);
    localStorage.setItem("search", data.search);
  };

  searchBooks(data) {
    console.log(data);
    API.getBooks(data)
      .then((res) => {
        var results = res.data.items;
        results.forEach((result) => {
          this.state.books.push(result.volumeInfo);
        });
        this.state.books.forEach((book) => {
          console.log(book.title);
        });
        console.log(this.state.books);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <div>
        <form>
          <input
            type="text"
            id="search"
            name="search"
            placeholder="Title or Author"
          />
          <button onClick={this.handleFormSubmit}> Button </button>
        </form>

        {this.state.books.map((book) => (
          <div className="col s12" key={book.publishedDate}>
            <p>{book.title}</p>
          </div>
        ))}
      </div>
    );
  }
}
