import React from "react";
import API from "../utils/booksapi";
import Results from "../components/Search";

export default class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
    };
  }

  componentDidMount() {
    document.getElementById("results").append(
      this.state.books.map((book) => (
        <div>
          <Results title={book.title} description={book.description} />
        </div>
      ))
    );
  }
  searchBooks(data) {
    console.log(data);
    API.getBooks(data)
      .then((res) => {
        var results = res.data.items;
        this.setState({ books: results });
        console.log(this.state.books);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  handleFormSubmit = (event) => {
    var data = { search: document.getElementById("search").value };
    event.preventDefault();
    this.searchBooks(data.search);
  };

  render() {
    return (
      <div>
        <form>
          <input
            type="text"
            id="search"
            name="search"
            placeholder="Title or Author"
          />
          <button onClick={this.handleFormSubmit}> Button </button>
        </form>

        <div id="results"></div>
      </div>
    );
  }
}
