import axios from "axios";
require("dotenv").config();

export default {
  getBooks: function (data) {
    console.log(data);
    return axios.get(
      `https://www.googleapis.com/books/v1/volumes?q=${data}&maxResults=5&key=${process.env.API_KEY}`
    );
  },
};
