import React from "react";
import API from "../utils/booksapi";
import Search from "../components/Search";

export default class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
    };
  }

  onSubmit(data) {
    console.log(data);
    searchBook(data);
  }

  searchBooks(data) {
    console.log(data);
    API.getBooks(data.search)
      .then((res) => {
        var results = res.data.items;
        results.forEach((result) => {
          this.state.books.push(result.volumeInfo);
        });
        console.log(this.state.books);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  componentDidMount() {}

  handleFormSubmit = (event) => {
    event.preventDefault();
    this.searchBooks();
  };

  render() {
    return (
      <div>
        <form>
          <input
            type="text"
            id="search"
            name="search"
            placeholder="Title or Author"
          />
          <button handleFormSubmit={this.handleFormSubmit}> Button </button>
        </form>
      </div>
    );
  }
}
