import React from "react";
import API from "../utils/booksapi";
import Search from "../components/Search";

export default class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
    };
  }

  onSubmit(data) {
    console.log(data);
    searchBook(data);
  }

  searchBook(data) {
    console.log(data);
    API.getBooks(data.search)
      .then((res) => {
        var results = res.data.items;
        results.forEach((result) => {
          books.push(result.volumeInfo);
        });
        console.log(books);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  componentDidMount() {}

  handleSubmit(data) {}

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            id="search"
            name="search"
            placeholder="Title or Author"
            ref={register({ required: true })}
          />
          <input type="submit" placeholder="search" />
        </form>
      </div>
    );
  }
}
